import React, { useState, useRef, useEffect } from 'react';
import { ReactComponent as DownArrow } from "./down-arrow.svg"
import { ReactComponent as UpArrow } from "./up-arrow.svg"
import Action from "./Action"



const Comment = ({ comment }) => {
  const [input, setInput] = useState("");
  const [editMode, setEditMode] = useState(false);
  const [showInput, setShowInput] = useState(false);

  const handleNewComment = () =>{
    setShowInput
  }


  const onAddComment = () => { };

  return (
    <div>
      <div className={comment.id === 1 ? "inputContainer" : "commentContainer"}>
        {comment.id === 1 ? (
          <>
            <input
              type="text"
              claassName="inputContainer__input first_input"
              autoFocus
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="type..."
            />
            <Action
              className="reply comment"
              type="COMMENT"
              handleClick={onAddComment}
            />

            <div className="reply comment" onClick={onAddComment}>
              COMMENT
            </div>
          </>
        ) : (<>

          <span style={{ wordWrap: "break-word" }}>{comment.name}</span>

          <div style={{ display: "flex", marginTop: "5px" }}>
            {editMode ? (
              <>
                <Action className="reply" type="SAVE" />
                <Action 
                className="reply" 
                type="CANCEL"
                handleClick={false}
                 />

              </>
            ) : (
              <>
                <Action className="reply" type="REPLY" handleClick={handleNewComment} />
                <Action
                  className="reply "
                  type="EDIT"
                  handleClick={() => {
                    setEditMode(true);
                  }}
                />
                <Action className="reply" type="DELETE" />

              </>)}


          </div>
        </>
        )}



      </div>
      <div style={{ paddingLeft: 25 }}>
      {
        showInput && (
          <div className="inputConatiner">
             <input
              type="text"
              claassName="inputContainer__input first_input"
              autoFocus
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder='text...'
              />
               <Action className="reply" type="REPLY" />
                <Action className="reply" type="CENCEL" />
          </div>
        )
      }

        {comment?.items?.map((cmnt) => {
          return <Comment key={cmnt.id} comment={cmnt} />;
        })}
      </div>
    </div>
  )
}

export default Comment
