import React,  {useState, useRef, useEffect } from 'react';
import{ ReactComponent as DownArrow} from "./down-arrow.svg"
import{ ReactComponent as UpArrow} from "./up-arrow.svg"



const Comment = ({ comment }) => {
    const [input, setInput] = useState("");

    const onAddComment = () => {};

  return (
    <div>
        <div>
            <input
                type="text" 
                claassName="inputContainer__input first_input" 
                autoFocus 
                placeholder="type..."
                value={input} 
                onChange={(e) => setInput(e.target.value)}
            />
            <Action
                className="reply comment"
                type="COMMENT"
                hand 
            />
        </div>
        <div className="reply comment" onClick={onAddComment}>COMMENT</div>
    </div>
  )
}

export default Comment
