import React, {useState, useEffect} from 'react'
import things from "../images/things.jpg";
import "./Bkprofile.css";
import {useParams} from 'react-router-dom';
import axios from 'axios';
import {BOOK_DETAILS_URL} from  "./API";
import "./bookdetails.css";


const BookDetails = () => {
    const [book, setBook] = useState({});

    const {id} = useParams();
  
    useEffect(() => {
        axios 
          .get(`${BOOK_DETAILS_URL}/${id}`)
          .then((res) => {
            setBook(res.data);
          })
          .catch((err) => console.log(err));
      }, [id]);

  return (
    <div className ="book_details_container">

      <div className="book_details_header">
        <h2 >{book?.title}</h2>
      </div>

      <div className="book_details_body">

        <div className="book_details_image">
            <img src={book?.image_url} at="#" />
        </div>

        <div className="book_details_content">
          
          <span className="section_header">Authors: </span>
          {book?.authors}

          <h2>Genres</h2>
          <p>{book?.genres}</p>
          
          <p>Number of pages:</p>
          {book?.num_pages}

          <h2>Description</h2>
          <p>{book?.description}</p>

        </div>

      </div>
    </div>
  )
}

export default BookDetails
