import React, { useState, useEffect } from 'react'
import { API_URL } from "./API";
import axios from "axios";
import { useNavigate } from 'react-router-dom';

import "./cols.css";



const Cols = ({ results, sorted, setSorted }) => {

  const [books, setBooks] = useState([]);
  const [category, setCategory] = useState([]);  
  const [sort, setSort] = useState("asc")
  const navigate = useNavigate();
  

  useEffect((e) => {

    axios
      .get("https://example-data.draftbit.com/books?_limit=50&&sort=${sort}")
      .then((res) => {
        console.log( res.data);
        setBooks(res.data);

       
      })
      .catch((err) => console.log(err));
  }, );

  const fetchData = (value) => {
    fetch("https://example-data.draftbit.com/books?_sort=$(sorted)")
      .then((response) => response.json())
      .then((json) => {
        const results = json.filter((item) => {
          return (
            value &&
            item &&
            item.title &&
            item.title.toLowerCase().includes(value)

          );
        });
        setResults(results);

      });
  };
  
  
  const descendingEvent = () => {
    let data = [...books]
    let result = data.sort((a,b) => a.title.localeCompare(b.title))
    setBooks(result)

  }



  
  return (
    <div className="collection_container">

      <div className="collection_header">
        <h1>Collection</h1>
        
      </div>
     
      <div className="order">
        <form action="#">
          <label htmlFor='sort'></label>
          <select name="sort">
            <option value="lowest" >Ascending:A-Z</option>
            <option   value="descending" onChange={() => setSort('desc')} > Descending:Z-A</option>


            
            <br />
          </select>
        </form>
      </div>

      <div className="body_area">
        <div className="filter_container">
          <div className="sort_list">
            <h2 className="side_bar_header">Category</h2>
            <div className="label_container">

            <label className="sidebar_label_container">
                <input type="radio" name="test" value="" onClick={(e) => setCategory(e.target.value)}/>
                <span className="checkmark">All</span>
              </label>

              <label className="sidebar_label_container">
                <input type="radio" name="test" value="Romance" onClick={(e) => setCategory(e.target.value)}/>
                <span className="checkmark">Romance</span>
              </label>

              <label className="sidebar_label_container">
                <input type="radio" name="test" value="Fantasy" onClick={(e) => setCategory(e.target.value)}/>
                <span className="checkmark">Fantasy</span>
              </label>

              <label className="sidebar_label_container">
                <input type="radio" name="test" value="Science Fiction" onClick={(e) => setCategory(e.target.value)}/>
                <span className="checkmark">Science Fiction</span>
              </label>

              <label className="sidebar_label_container">
              <input type="radio" name="test"  value="Classics" onClick={(e) => setCategory(e.target.value) }/>
                <span className="checkmark">Classics</span>
              </label>

              <label className="sidebar_label_container">
                <input type="radio" name="test"  value="Young Adult" onClick={(e) => setCategory(e.target.value) }/>
                <span className="checkmark">Young Adult</span>
              </label>
            </div>
          </div>

          <div className="sort_list">
            <h2 className="side_bar_header">Time</h2>
            <div className="label_container">
              <label className="sidebar_label_container">
                <input type="radio" name="test" />
                <span className="checkmark">New Arrivals</span>
              </label>
            </div>
          </div>
        </div>


        <div className="content_area">
          <div className="book_list">
         
          </div>
          {(results == "") ?
             <div>
             <div className="book_list">
               {books.map((book) => (
                 book.genre_list?.includes(category) === true ? 
                 <div key={book.id}>      
                   <h2>{book.title}</h2>
                   <img src={book.image_url} alt="#"
                     onClick={() => navigate(`/book/${book.id}`)}
                   />
                   <button>Add to Favorites</button>
                 </div> : <></>
               ))}
             </div>
           </div>

            :
            <div>
            <div className="book_list">
              {results.map((result) => (
                result.genre_list?.includes(category) === true ? 

                <div key={result.id}>
                  

                  <h2>{result.title}</h2>
                  <img src={result.image_url} alt="#"
                    onClick={() => navigate(`/book/${result.id}`)}
                  />
                  <button>Add to Favorites</button>
                </div> : <></>
              ))}
            </div>
          </div>

            



          }
        </div>
      </div>
    </div>
  )

}










export default Cols;
