import React, {useState, useEffect} from 'react'
import {API_URL} from "./API";
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import iron from "../images/iron.jpg"
import atomic from "../images/atomic.jpg"
import fourth from "../images/fourth.jpg"
import just from "../images/just.jpg"
import shadow from "../images/shadow.jpg"
import things from "../images/things.jpg"
import "./cols.css";


const Cols = () => {

  const [books, setBooks] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {
    axios 
      .get(API_URL)
      .then((res) => {
        console.log(res.data);
        setBooks(res.data);
      })
      .catch((err) => console.log(err));
  }, []);
  

return (
  <div class="collection_box">

    <div className="collection_header">
      <h1>Collection</h1>
    </div>


    <div className="order">
      <form action="#">
        <label htmlFor='sort'></label>
        <select name="sort" id="sort">
          <option value="lowest">Ascending:A-Z</option>
          <option value="lowest">Descending:Z-A</option>
          <br />
        </select>
      </form>
    </div>


  <div className="body">
    <div className="sort_area">
    APPLES

    



    
    </div>
    <div className="content_area">
  ORANGE
    </div>
  </div>



    <div className="">
      <div className="book_list">
        {books.map((book) => (
          <div key={book.id}>
            <div>
              <h2>{book.title}</h2>
            </div>
              <img src={book.image_url} alt="#" 
              onClick={() => navigate(`/book/${book.id}`)}
              />
            
            <button>Add to Favorites</button>
          </div>
        ))}
      </div>  
    </div> 
</div>
  )
        
}

 

export default Cols;
