import React, { useState, useEffect } from 'react'
import { API_URL } from "./API";
import axios from "axios";
import { useNavigate } from 'react-router-dom';

import "./cols.css";



const Cols = ({ results }) => {

  const [books, setBooks] = useState([]);
  const [category, setCategory] = useState([]);

  const handleFilter =  () => {
    {
       books.map(
        (book) =>
         { book.genre_list.includes("Romance") === true? 
      ( <></>): <div>STILL DONE</div>}
         
        )}

  };

  


  
  const navigate = useNavigate();

  useEffect(() => {
    axios
      .get(API_URL)
      .then((res) => {
        console.log("???", res.data);
        setBooks(res.data);
        const genres = JSON.stringify(books)
        const genre = genres.split(",")
        console.log(genre)
      })
      .catch((err) => console.log(err));
  }, []);

  




  
  return (
    <div className="collection_container">

      <div className="collection_header">
        <h1>Collection</h1>
        
      </div>
     
      <div className="order">
        <form action="#">
          <label htmlFor='sort'></label>
          <select name="sort" id="sort">
            <option value="lowest">Ascending:A-Z</option>
            <option value="lowest">Descending:Z-A</option>
            <br />
          </select>
        </form>
      </div>

      <div className="body_area">
        <div className="filter_container">
          <div className="sort_list">
            <h2 className="side_bar_header">Category</h2>
            <div className="label_container">

            <label className="sidebar_label_container">
                <input type="radio" name="test" value="All" onClick={(e) => setCategory(e.target.value)}/>
                <span className="checkmark">All</span>
              </label>

              <label className="sidebar_label_container">
                <input type="radio" name="test" value="Romance" onClick={(e) => setCategory(e.target.value)}/>
                <span className="checkmark">Romance</span>
              </label>

              <label className="sidebar_label_container">
                <input type="radio" name="test" value="Fantasy" onClick={(e) => setCategory(e.target.value)}/>
                <span className="checkmark">Fantasy</span>
              </label>

              <label className="sidebar_label_container">
                <input type="radio" name="test" value="Non-Fiction" onClick={(e) => setCategory(e.target.value)}/>
                <span className="checkmark">Non-Fiction</span>
              </label>

              <label className="sidebar_label_container">
                <input className="check" type="radio" name="test "  value="History" onClick={(e) => setCategory(e.target.value)}/>
                <span className="checkmark">History</span>
              </label>

              <label className="sidebar_label_container">
                <input type="radio" name="test" />
                <span className="checkmark">Comedy</span>
              </label>
            </div>
          </div>

          <div className="sort_list">
            <h2 className="side_bar_header">Time</h2>
            <div className="label_container">
              <label className="sidebar_label_container">
                <input type="radio" name="test" />
                <span className="checkmark">New Arrivals</span>
              </label>
            </div>
          </div>
        </div>


        <div className="content_area">
          <div className="book_list">
          {(true)  ? 
            <div> 
               {books &&
      books.map(
        (book) =>
         <p> { book.genre_list.includes('Dystopia') === true? <div><h2>{book.title}</h2>}
         <img src={book.image_url} alt="#" onClick={() => navigate(`/book/${book.id}`)} />
         <button>Add to Favorites</button></div>: <div></div>}</p>
         
      )}
            </div> 
          : ( 7==5 ) ? <div>send</div>
          : <div>search</div>
        
          } 
          </div>

<>hmmm</>

          {(results == "") ?
            <div>
              <div className="book_list">
                {books.map((book) => (
                  <div key={book.id}>
                
                    <h2>{book.title}</h2>
                    <img src={book.image_url} alt="#"
                      onClick={() => navigate(`/book/${book.id}`)}
                    />
                    <button>Add to Favorites</button>
                  </div>
                ))}
              </div>
            </div>

            :
            <div className="book_list">
              {
              
              results.map((result, id) => {
                return <div key={id}>
                  {<h2>{results.title}</h2>}
                  <img src={result.image_url} alt="#" onClick={() => navigate(`/book/${results.id}`)} />
                  <button>Add to Favorites</button>
                </div>
              })}
            </div>

            
           // to string inc;udes



          }
        </div>
      </div>
    </div>
  )

}








export default Cols;
