import React, { useState, useEffect } from 'react'
import "./Home.css";
import iron from "../images/iron.jpg"
import atomic from "../images/atomic.jpg"
import fourth from "../images/fourth.jpg"
import just from "../images/just.jpg"
import shadow from "../images/shadow.jpg"
import things from "../images/things.jpg"
import axios from "axios";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import { useParams } from 'react-router-dom';
import { BOOK_DETAILS_URL } from "./API";
import Featured from "./Featured";




const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 5
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1
  }
};



const Home = () => {

  const [books, setBooks] = useState([]);
  const bookIDs = ["10", "25", "15", "7"]
  const { id } = useParams();


  useEffect((e) => {
    axios
      .get("https://example-data.draftbit.com/books?_limit=50")
      .then((res) => {
        console.log("???", res.data);
        setBooks(res.data);
        filterItems(res.data);

      })
      .catch((err) => console.log(err));
  }, [id]);

  const filterItems = (items) => {
    const allowedIds = [1, 2, 3]; // Define your allowed IDs here
    const filtered = items.filter((item) => allowedIds.includes(item.id));
    setFilteredData(filtered);
 };






  return (


    <div className="home_container">
      <div className='wrapper'>
        <div className="text">
          <p>
          </p>

        </div>
      </div>

      <div className="featured">
        Featured Book

        <h1>Featured Book</h1>
        <Carousel showDots={true} responsive={responsive}>

          {
            books.map((item, id=5) => {
              return <div className="c">
                {(item.id == "1") || (item.id == "2") || (item.id == "2") || (item.id == "4") ?
                  <div key={item.id}>
                    <img src={item.image_url} alt="#" />
                    <h2>{item.title}</h2>
                  </div>
                  : <></>

                }

                
              </div>
            })
          }
          
        </Carousel>


      </div>

    </div>


  )
}

export default Home
