import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom';
import { BOOK_DETAILS_URL } from "./API";
import axios from 'axios';
import "./bookdetails.css";
import Comment from "./Comment"
import useNode from "./useNode"
import { ReactComponent as DownArrow } from 'react';
import { ReactComponent as UpArrow } from 'react';
import "./styles.css"

const comments = {
  id: 1,
  items: [
    {
      id: 1234561,
      name: "hello",
      items: [
        {
          id: 1234562,
          name: "hello, world",
          items: [
            {
              id: 1234563,
              name: "hello, world 123",
              items: []
            }
          ]
        }
      ]
    },
    { 
      id: 1234564,
      name: "react js",
      items: [
        {
          id: 1234565,
          name: "javascript",
          items: []
        }
      ]


    }
  ]
};

const BookDetails = () => {
  const [book, setBook] = useState({});
  const { id } = useParams();
  const [commentsData, setCommentsData] = useState(comments);
  const {insertNode, editNode, deleteNode} = useNode();


  const handleInsertNode = (folderId, item ) => {
    const finalStructure = insertNode(commentsData, folderId, item)
    setCommentsData(finalStructure);
  }

  const handleEditNode = (folderId, item ) => {
    const finalStructure = editNode(commentsData, folderId, item)
    setCommentsData(finalStructure);
  }

  const handleDeleteNode = (folderId, item ) => {
    const finalStructure = deleteNode(commentsData, folderId, item)
    setCommentsData(...finalStructure);
    set
  }

  useEffect(() => {
    axios
      .get(`${BOOK_DETAILS_URL}/${id}`)
      .then((res) => {
        setBook(res.data);
      })
      .catch((err) => console.log(err));
  }, [id]);

  return (
    <div className="book_details_container">

      <div className="book_details_header">
        <h2 >{book?.title}</h2>
      </div>

      <div className="book_details_body">

        <div className="book_details_image">
          <img src={book?.image_url} alt="#" />
        </div>

        <div className="book_details_content">


          <div className="section">
            <span className="section_header">Authors: </span>
            <span className="book_info">{book?.authors}</span>
          </div>


          <div className="section">
            <span className="section_header">Genres: </span>
            <span className="book_info">{book?.genres}</span>
          </div>

          <div className="section">
            <span className="section_header">Num of pages: </span>
            <span className="book_info">{book?.num_pages}</span>
          </div>

          <div className="section">
            <p className="section_header"  >Description: </p>
            <span className="book_info" >{book?.description}</span>
          </div>

        </div>

      </div>

      <div>
      <Comment
        handleInsertNode={handleInsertNode}
        handleEditNode={handleEditNode}
        handleDeleteNode={handleDeleteNode}
        comment={commentsData} />
    </div>
    </div>
  )
}

export default BookDetails
