import React, {useState, useEffect} from 'react'
import "./Home.css";
import iron from "../images/iron.jpg"
import atomic from "../images/atomic.jpg"
import fourth from "../images/fourth.jpg"
import just from "../images/just.jpg"
import shadow from "../images/shadow.jpg"
import things from "../images/things.jpg"
import axios from "axios";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import {useParams} from 'react-router-dom';
import {BOOK_DETAILS_URL} from  "./API";



const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 3
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1
  }
};



const Home = () => {

  const [books, setBooks] = useState([]);


    const {id} = useParams();
  
    useEffect((e) => {
      axios
        .get("https://example-data.draftbit.com/books?_limit=50")
        .then((res) => {
          console.log("???", res.data);
          setBooks(res.data);
        })
        .catch((err) => console.log(err));
    }, []);

  return (


    <div className="home_container">
      <div className='wrapper'>
        <div className="text">
          <p>
          </p>

        </div>
      </div>

      <div className="featured">
        Featured Book
        <div className="book_list">
              {results.map((result) => (
                result.genre_list?.includes(category) === true ? 

                <div key={result.id}>
                  

                  <h2>{result.title}</h2>
                  <img src={result.image_url} alt="#"
                    onClick={() => navigate(`/book/${result.id}`)}
                  />
                  <button>Add to Favorites</button>
                </div> : <></>
              ))}
            </div>
      
        <Carousel responsive={responsive} className="carousel">
          <div className="image_container"><img src={iron} alt="#" /></div>
          <div><img src={atomic} alt="#" /></div>
          <div><img src={fourth} alt="#" /></div>
          <div><img src={shadow} alt="#" /></div>
          <div><img src={things} alt="#" /></div>
          <div><img src={just} alt="#" /></div>
        </Carousel>
      </div>

    </div>


  )
}

export default Home
