const useNode = () => {
    const insertNode = function (tree, commentId, item) {
        if(tree.id === commentId){
            tree.items.push({
                id: new Date().getTime(),
            })
        }
    };

    const editNode = (tree, id) => {};

    const deleteNode = (tree, id) => {};

    return {insertNode, editNode, deleteNode };
}

export default useNode;
