import React, { useState, useEffect } from 'react'
import "./Home.css";
import iron from "../images/iron.jpg"
import atomic from "../images/atomic.jpg"
import fourth from "../images/fourth.jpg"
import just from "../images/just.jpg"
import shadow from "../images/shadow.jpg"
import things from "../images/things.jpg"
import axios from "axios";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
import { useParams } from 'react-router-dom';
import { BOOK_DETAILS_URL } from "./API";
import Featured from "./Featured";




const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 3
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1
  }
};



const Home = () => {

  const [books, setBooks] = useState([]);
  const bookIDs = ["10", "25", "15", "7"]
  const { id } = useParams();


  useEffect((e) => {
    axios
      .get("https://example-data.draftbit.com/books?_limit=50")
      .then((res) => {
        console.log("???", res.data);
        setBooks(res.data);
      })
      .catch((err) => console.log(err));
  }, []);

  return (


    <div className="home_container">
      <div className='wrapper'>
        <div className="text">
          <p>
          </p>

        </div>
      </div>

      <div className="featured">
        Featured Book

        <div className="book_list">
          <Carousel responsive={responsive} className="carousel">
            {books.map((result) => (

              (result.id == "25") || (result.id == "17") || (result.id == "5") || (result.id == "57") ?
                <div key={result.id}>

                  <img src={result.image_url} alt="#" />



                </div>
                : <></>


            )
            )}
          </Carousel>

        </div>



      </div>

    </div>


  )
}

export default Home
