import React, { useState } from 'react';
import "./account.css";
import Middleware from './Middleware';
import { useNavigate } from 'react-router-dom';


const Account = () => {
    const [action, setAction] = useState("Sign Up");
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const [email, setEmail] = useState("");

    const navigate = useNavigate();

    const handleSubmit = (event) => {
        event.preventDefault();
        const user = {
            username,
            email,
            password
        };
        //Outputs user to console
        console.log(user)
        ////Try is used to handle synchronous errors that might occur in the code within the try block
        try {
            Middleware.register(user).then(
                async (response) => {
                    console.log(response.data)
                    //Outputs the data from the response to the console.
                    // store the user in localStorage
                    localStorage.setItem('user', JSON.stringify(response.data))
                    console.log(response.data)
                    // navigate("/")
                    window.location.reload();
                }
            )
        }
        catch (error) {
            console.log("Invaild Email or Password", error)
        }
        // Handle form submission here (e.g., authentication)
    }

    const handleLogin = (event) => {
        event.preventDefault();
        const user = {
            email,
            password
        };
        //Outputs user to console
        console.log(user)
        ///Try is used to handle errors that might occur
        try {
            Middleware.login(user).then(
                async (response) =>{
                    console.log(response.data)
                    //Outputs the data from the response to the console

                    localStorage.setItem('user', JSON.stringify(response.data))
                    console.log(response.data)
                    //navigate("/")
                    window.location.reload();
                }
            )
        }
        catch (error) {
            console.log("Invalid Email or Password", error)
        }
        //Handle form submission here (e.g., authentication)
    }

    return (
        <div className="account_container">
            <div className="account_header">
                <div className="text">{action}</div>
                <div className="underline"></div>

            </div>
            <div className="inputs">
                {action === "Login" ? <div></div> : <div className="input">
                    <input type="text" placeholder="Name" value={username} onChange={e => setUsername(e.target.value)} />
                </div>}

                <div className="input">
                    <input value={email} onChange={e => setEmail(e.target.value)} type="email" placeholder="Email Id" />
                </div>
                <div className="input">
                    <input value={password} onChange={e => setPassword(e.target.value)} type="password" placeholder="Password" />
                </div>
            </div>
            {action === "Sign Up" ? <div></div> : <div className="forgot-password">Lost Password? <span>Click Here!</span></div>
            }
            <div className="submit-container">
                <div className={action === "Login" ? "submit gray" : "submit"} onClick={(event) => {handleSubmit(event); setAction("Submit")}}>Submit</div>
                <div className={action === "Login" ? "submit gray" : "submit"} onClick={() => { setAction("Sign Up") }}>Sign Up</div>
                <div className={action === "Sign Up" ? "submit gray" : "submit"} onClick={(event) => {handleLogin(event); setAction("Login")}}>Login</div>
            </div>
        </div>
    )
}

export default Account
