import React, {useState, useEffect} from 'react'
import things from "../images/things.jpg";
import "./Bkprofile.css";
import {useParams} from 'react-router-dom';
import axios from 'axios';
import {BOOK_DETAILS_URL} from  "./API";
import "./bookdetails.css";


const BookDetails = () => {
    const [book, setBook] = useState({});

    const {id} = useParams();
  
    useEffect(() => {
        axios 
          .get(`${BOOK_DETAILS_URL}/${id}`)
          .then((res) => {
            setBook(res.data);
          })
          .catch((err) => console.log(err));
      }, [id]);

  return (
    <div className ="book_details_container">

      <div>
        
      </div>
        
        <div>
            <h2 className="book_details_header">{book?.title}</h2>
            <img src={book?.image_url} at="#" />
        </div>
        <div className="book_details_content">
            <h2>Description</h2>
            <p>{book?.description}</p>
            <h2>Autors</h2>
            <p>{book?.authors}</p>
            <h2>Genres</h2>
            <p>{book?.genres}</p>



        </div>
    </div>
  )
}

export default BookDetails
