import React, { useState } from 'react';
import "./account.css";
import Middleware from './Middleware';
import { useNavigate } from 'react-router-dom';
import * as PropTypes from "prop-types";


class Account extends React.Component {
    render() {
        let {
            handleLogin,
            handleSubmit,
            currentUser,
            isLoggedIn,
            onUserNameChange,
            onPasswordChange,
            onEmailChange
        } = this.props;
        const [action, setAction] = useState('');


        const navigate = useNavigate();


        return (
            <div className="account_container">
                <div className="account_header">
                    <div className="text">{action}</div>
                    <div className="underline"></div>

                </div>
                <div className="inputs">
                    {action === "Login" ? <div></div> : <div className="input">
                        <input type="text" placeholder="Name" onChange={(e) => {
                            onUserNameChange(e.target.value)
                        }}/>
                    </div>}

                    <div className="input">
                        <input onChange={(e) => {
                            onEmailChange(e.target.value)
                        }} type="email" placeholder="Email Id"/>
                    </div>
                    <div className="input">
                        <input onChange={(e) => {
                            onPasswordChange(e.target.value)
                        }} type="password" placeholder="Password"/>
                    </div>
                </div>
                {action === "Sign Up" ? <div></div> :
                    <div className="forgot-password">Lost Password? <span>Click Here!</span></div>
                }
                <div className="submit-container">
                    <div className={action === "Login" ? "submit gray" : "submit"} onClick={(event) => {
                        handleSubmit(event);
                        setAction("Submit")
                    }}>Register
                    </div>
                    {/*<div className={action === "Login" ? "submit gray" : "submit"} onClick={() => { setAction("Sign Up") }}>Sign Up</div>*/}
                    <div className={action === "Sign Up" ? "submit gray" : "submit"} onClick={(event) => {
                        handleLogin(event);
                        setAction("Login")
                    }}>Login
                    </div>
                </div>
            </div>
        )
    }
}

Account.propTypes = {
    handleLogin: PropTypes.any,
    handleSubmit: PropTypes.any,
    currentUser: PropTypes.any,
    isLoggedIn: PropTypes.any,
    onUserNameChange: PropTypes.any,
    onPasswordChange: PropTypes.any,
    onEmailChange: PropTypes.any
}

export default Account
