import React, {useState, useEffect} from 'react'
import "./Home.css";
import image from "../images/img2.jpg"
import iron from "../images/iron.jpg"
import atomic from "../images/atomic.jpg"
import fourth from "../images/fourth.jpg"
import just from "../images/just.jpg"
import shadow from "../images/shadow.jpg"
import things from "../images/things.jpg"
import {API_URL} from "./API";
import axios from "axios";
import { useNavigate } from 'react-router-dom';
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";



const responsive = {
  superLargeDesktop: {
    // the naming can be any, depends on you.
    breakpoint: { max: 4000, min: 3000 },
    items: 5
  },
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 3
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1
  }
};



const Home = () => {

  const [book, setBook] = useState({});

    const {id} = useParams();
  
    useEffect(() => {
        axios 
          .get(`${BOOK_DETAILS_URL}/${id}`)
          .then((res) => {
            setBook(res.data);
          })
          .catch((err) => console.log(err));
      }, [id]);
  return (


    <div>
      <div className='wrapper'>
        <div className="text">
          <p>

          </p>

        </div>
      </div>

      <div className="featured">
        Featured Books
      

      
      
        <Carousel responsive={responsive} className="carousel">
          <div><img src={iron} alt="book picture" /></div>
          <div><img src={atomic} alt="book picture" /></div>
          <div><img src={fourth} alt="book picture" /></div>
          <div><img src={shadow} alt="book picture" /></div>
          <div><img src={things} alt="book picture" /></div>
          <div><img src={just} alt="book picture" /></div>

        </Carousel>
      </div>

    </div>


  )
}

export default Home
